import React, { Component } from 'react';
import VolOff from '../../assets/voloff.png';

export default class Sound extends Component {


  toggleSound = () => {
    this.props.sound === "on" ?
    this.changeSoundAttributes("off",
      {VolOff}) :
    this.changeSoundAttributes("on",
      process.env.PUBLIC_URL 
      + "/assets/vol.png")
  }

  changeSoundAttributes = ( toggle, src, e) => {
      this.props.setSound(toggle)
      e.target.src = src
  }

  render() {
    return (
        <button
         onClick={(e) => {
           this.toggleSound(e)}}>
        <img style={{width:"30px"}} src={process.env.PUBLIC_URL
        + "/assets/vol.png"}/>
        </button>
    )
  }
}
